import React, { useEffect, useState } from "react";
import mondaySdk from "monday-sdk-js";
import './App.css';
//https://23332f08554e13c5.cdn2.monday.app
//http://localhost:3000
function App() {
  const monday = mondaySdk();
  const [isauthorized, setIsAuthorized] = useState(false);
  const [isxeroauthorized, setIsXeroAuthorized] = useState(false);

  const HandleXeroLogin = () => {

    // Redirect users to Monday.com's authorization URL
    // window.location.href = `https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod/connect`;

    const xeroAuthWindow = window.open(
      "https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod/connect",
      "xeroAuthWindow",
      "width=600,height=600"
    );

    // Monitor the new window for changes in the URL
    const checkWindowClosed = setInterval(() => {
      if (!xeroAuthWindow || xeroAuthWindow.closed) {
        clearInterval(checkWindowClosed);
        // After the Xero window is closed or authorization is complete,
        // redirect back to the Monday.com iframe
        monday.execute("navigate", { location: "xero1" });
      }
    }, 1000);
  }

  useEffect(() => {

    const urlParams = new URLSearchParams(window.location.search);
    const authorized = urlParams.get("code");

    if (authorized) {
      fetch(`https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod/callback?code=${authorized}`)
      .then((res) => res.json())
      .then((data) => {

        const accessToken = data.access_token;

        monday.setToken(accessToken);


        if (accessToken) {
          console.log(accessToken);
          setIsXeroAuthorized(true);


        }

      })
      .catch((error) => {
        console.error("Error exchanging code for tokens:", error);

      });

    }

  }, );

  const HandleLogin = () => {

    // Redirect users to Monday.com's authorization URL
    window.location.href = `https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod//auth`;

  }

  useEffect(() => {
    // After redirect from Monday.com authorization, handle the received code
    const urlParams = new URLSearchParams(window.location.search);
    const authorizationCode = urlParams.get("code");


    if (authorizationCode) {


      // Exchange the authorization code for an access token and refresh token
      fetch(`https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod/monday-callback?code=${authorizationCode}`)
        .then((res) => res.json())
        .then((data) => {

          const accessToken = data.access_token;

          monday.setToken(accessToken);


          if (accessToken) {
            console.log(accessToken);
            setIsAuthorized(true);


          }

        })
        .catch((error) => {
          console.error("Error exchanging code for tokens:", error);

        });
    }


  }, [monday]);









  return (
    <div>
      {isxeroauthorized ? (
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-6 form-container">
              <center>
                <h3>Connect to Monday!</h3>
              </center>
              <button onClick={HandleLogin} className="btn btn-primary">
                Connect to monday
              </button>
            </div>
          </div>
        </div>
      ) : isauthorized ? (
        <div><h1>hello bc</h1></div>
      ) : (
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-6 form-container">
              <center>
                <h3>Connect to xero!</h3>
              </center>
              <button onClick={HandleXeroLogin} className="btn btn-primary">
                Connect to xero
              </button>
            </div>
          </div>
        </div>

      )}
    </div>
  );

};
export default App;
