import React, { useEffect, useState } from "react";
import mondaySdk from "monday-sdk-js";
import './App.css';
//https://23332f08554e13c5.cdn2.monday.app
//http://localhost:3000
function App() {
  const monday = mondaySdk();
  const [isauthorized, setIsAuthorized] = useState(false);
  const [isxeroauthorized, setIsXeroAuthorized] = useState(false);
  const [invoices, setInvoices]= useState({});
  const[token, setToken]= useState('');
  let columnIDs = [];
  const HandleXeroLogin = () => {

    // window.top.location.href = `https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod/connect`;
    window.location.href= 'http://localhost:3001/connect';
  }

  useEffect(() => {

    const urlParams = new URLSearchParams(window.location.search);
    const authorized = urlParams.get("code");

    if (authorized) {
      fetch(`http://localhost:3001/callback?code=${authorized}`)
      .then((res) => res.json())
      .then((data) => {

        const accessToken = data.tokenSet.access_token;

        monday.setToken(accessToken);


        if (accessToken) {

          setIsXeroAuthorized(true);
          setToken(accessToken);

        }

      })
      .catch((error) => {
        console.error("Error exchanging code for tokens:", error);

      });

    }

  }, );



  const HandleLogin = () => {

    // Redirect users to Monday.com's authorization URL
    // window.location.href = `https://i9j279set4.execute-api.us-east-1.amazonaws.com/prod//auth`;
       window.location.href = 'http://localhost:3001/auth';
  }

  useEffect(() => {
    // After redirect from Monday.com authorization, handle the received code
    const urlParams = new URLSearchParams(window.location.search);
    const authorizationCode = urlParams.get("code");


    if (authorizationCode) {


      // Exchange the authorization code for an access token and refresh token
      fetch(`http://localhost:3001/monday-callback?code=${authorizationCode}`)
        .then((res) => res.json())
        .then((data) => {

          const accessToken = data.access_token;

          monday.setToken(accessToken);


          if (accessToken) {
            setToken(accessToken);
            setIsAuthorized(true);


          }

        })
        .catch((error) => {
          console.error("Error exchanging code for tokens:", error);

        });
    }


  }, [monday]);

  const fetchInvoices = () => {


    fetch(`http://localhost:3001/invoice`)
      .then((res) => res.json())
      .then((data)=>{
        console.log(data.invoices);
        setInvoices(data.invoices);
        const Invoices = data.invoices;
        // createColumns();
        Invoices.forEach((invoice) => {
          //board_id:1816973150
          const columnValues=[];
          columnIDs.forEach(({ id, columnName }) => {
            columnValues.push(`\\"${id}\\":\\"${invoice[columnName.toLowerCase().replace(/\s/g, '')]}\\"`);
          });

          const columnValuesString = `{${columnValues.join(',')}}`;


          const query = `mutation {
          create_item(
            board_id: 1816973150,

            item_name: "Invoices",
           column_values: "${columnValuesString}"
          ) {
            id
          }
        }`;

          fetch("https://api.monday.com/v2", {
            method: "post",
            headers: {
              "Content-Type": "application/json",
              'Authorization': `${token}`,
            },
            body: JSON.stringify({
              query: query,
            }),
          })
            .then((res) => res.json())
            .then((res) => console.log(JSON.stringify(res, null, 2)));
        });

      })

      .catch((error) => {
        console.error("Error fetching invoices:", error);
      });
  };

  const createColumns = () => {
    const columnsToCreate = ['Invoice Number', 'Reference', 'Status', 'Amount Due'];

    columnsToCreate.forEach(columnName => {
      const query = `
        mutation {
          create_column (
            board_id: 1816973150, 
            title: "${columnName}",
            column_type: text
          ) {
            id
          }
        }
      `;

      fetch("https://api.monday.com/v2", {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${token}`, // Replace with your actual Monday API key
        },
        body: JSON.stringify({
          'query': query
        })
      })
      .then(res => res.json())
      .then(res => {
        const { id } = res.data.create_column; 
        console.log(`Created column '${columnName}':`, id);
        columnIDs.push({ columnName, id });
      })
      .catch(error => {
        console.error(`Error creating column '${columnName}':`, error);
      });
    });


  };



  return (
    <div>
      {isxeroauthorized ? (
         <div className="container mt-5 mx-auto">
         <div className="row justify-content-center align-items-center">
           <div className="col-lg-6 mb-5 mb-lg-0 ">
             <div class="card ">
               <div class="card-body fw-bold py-5 px-md-5 shadow-lg">
                 <h5 class="card-title">Monday Authorization</h5>
                 <button onClick={HandleLogin} className="btn btn-primary">
                Connect to monday
              </button>
               </div>
             </div>
           </div>
         </div>
       </div>
      ) : isauthorized ? (
        <>
         <nav class="navbar navbar-expand-md navbar-light bg-dark pt-3 pb-3  mb-5">
            <div class="container-xxl">
              <img
                src="./favicon.ico" // Use the path to your favicon.ico file
                alt="Logo"
                className=" me-2" // Add a margin to separate the logo from the text
              />
              <span class="text-light fs-4 fw-bold">Xero Integration</span>

              <div
                class="collapse navbar-collapse justify-content-end align-center "
                id="main-nav"
              >
                <ul class="navbar-nav ">
                  <li class="nav-item ms-2 d-none d-md-inline me-2">
                  <button onClick={fetchInvoices} className="btn btn-primary">
                Fetch Invoices
              </button>
                  </li>
                  <li>
                  <button onClick={createColumns} className="btn btn-primary">
                Connect to monday
              </button>
                  </li>
                </ul>
              </div>
            </div>
          </nav>


          <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Invoice Number</th>
                <th>Amount Due</th>
                <th>Reference</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(invoices).map((key) => (
                <tr key={key}>
                  <td>{invoices[key].invoiceNumber}</td>
                  <td>{invoices[key].amountDue}</td>
                  <td>{invoices[key].reference}</td>
                  <td>{invoices[key].status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        </>
      ) : (
        <div className="container mt-5 mx-auto">
        <div className="row justify-content-center align-items-center">
          <div className="col-lg-6 mb-5 mb-lg-0 ">
            <div class="card ">
              <div class="card-body fw-bold py-5 px-md-5 shadow-lg">
                <h5 class="card-title">Xero Authorization</h5>
                <button onClick={HandleXeroLogin} className="btn btn-primary">
               Connect to Xero
             </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      )}
    </div>
  );

};
export default App;
