import React, { useEffect } from "react";
import mondaySdk from "monday-sdk-js";
import './App.css';

function App() {
  const monday = mondaySdk();

  const handleLogin = () => {
    // Redirect users to Monday.com's authorization URL
    window.location.href = `https://auth.monday.com/oauth2/authorize?client_id=5ee2a94811e118cfd48f9522978bc103&redirect_uri=http://localhost:3000&scope=me:read boards:read boards:write users:read users:write&response_type=code`;
  };

  useEffect(() => {
    // After redirect from Monday.com authorization, handle the received code
    const urlParams = new URLSearchParams(window.location.search);
    const authorizationCode = urlParams.get("code");

    if (authorizationCode) {
      // Exchange the authorization code for an access token and refresh token
      fetch("https://auth.monday.com/oauth2/token", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          client_id: "5ee2a94811e118cfd48f9522978bc103",
          client_secret: "604f06251e19d00d2b09d6a7f225abfc",
          code: authorizationCode,
          redirect_uri: "http://localhost:3000",
          grant_type: "authorization_code",
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          const accessToken = data.access_token;
          const refreshToken = data.refresh_token;

          // Use accessToken as needed in your Monday.com SDK calls
          monday.setToken(accessToken);

          // Store tokens securely (localStorage, state management, etc.)
          // ...

          // Redirect to your app's main page or perform necessary actions
          window.location.href = "/main"; // Redirect to main page
        })
        .catch((error) => {
          console.error("Error exchanging code for tokens:", error);
          // Handle errors
        });
    }
  }, [monday]);

  return (
    <div>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 form-container">
            <center>
              <h3>Welcome to QuickBooks Integration!</h3>
            </center>
            <button onClick={handleLogin} className="btn btn-primary">
              Login with Monday.com
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
