import React, { useEffect, useState } from "react";
import mondaySdk from "monday-sdk-js";
import './App.css';
//https://23332f08554e13c5.cdn2.monday.app
//http://localhost:3000
function App() {
  const monday = mondaySdk();
  const [isauthorized, setIsAuthorized]= useState(false); 
  const redirect_uri= document.getElementById("iframe_id").contentWindow.location.href;
  const HandleLogin = () => {

    // Redirect users to Monday.com's authorization URL
    window.location.href = `https://auth.monday.com/oauth2/authorize?client_id=5ee2a94811e118cfd48f9522978bc103&redirect_uri=${redirect_uri}&scope=me:read boards:read boards:write users:read users:write&response_type=code`;
  }
    useEffect (()=>{
    // After redirect from Monday.com authorization, handle the received code
    const urlParams = new URLSearchParams(window.location.search);
    const authorizationCode = urlParams.get("code");

    if (authorizationCode) {


      // Exchange the authorization code for an access token and refresh token
      fetch("/oauth2/token", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",

        },
        body: JSON.stringify({
          client_id: "5ee2a94811e118cfd48f9522978bc103",
          client_secret: "604f06251e19d00d2b09d6a7f225abfc",
          code: authorizationCode,
          redirect_uri: redirect_uri,
          grant_type: "authorization_code",
        }),
      })
        .then((res) => res.json())
        .then((data) => {

          const accessToken = data.access_token;

          monday.setToken(accessToken);


          if(accessToken){
            console.log(accessToken);
          setIsAuthorized(true);

        }

        })
        .catch((error) => {
          console.error("Error exchanging code for tokens:", error);

        });
    }
  },[]);



  return (
    <div>
      {isauthorized ? ( 
        <div> <h1>
          hello world
          </h1></div>
      ): (
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 form-container">
            <center>
              <h3>Welcome to QuickBooks Integration!</h3>
            </center>
            <button onClick={HandleLogin} className="btn btn-primary">
              Login with Monday.com
            </button>
          </div>
        </div>
      </div>
   )}
    </div>
  );
};   
export default App;
