import React, { useEffect, useState } from "react";
import axios from 'axios';
import './App.css';
import LandingPage from "./components/LandingPage.js"
import MondayCard  from "./components/MondayCard.js"
//https://23332f08554e13c5.cdn2.monday.app
//http://localhost:3000
function App() {
  const monday = mondaySdk();
  const [isauthorized, setIsAuthorized] = useState(false);
  const [isxeroauthorized, setIsXeroAuthorized] = useState(false);
  const [token, setToken] = useState('');
  let columnIDs = [];




  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const authorized = urlParams.get('code');

    if (authorized) {
      axios.get(`http://localhost:3001/callback?code=${authorized}`)
        .then((response) => {
          const accessToken = response.data.tokenSet.access_token;
          monday.setToken(accessToken);

          if (accessToken) {
            setIsXeroAuthorized(true);
            setToken(accessToken);
          }
        })
        .catch((error) => {
          console.error('Error exchanging code for tokens:', error);
        });
    }

  }, [monday]);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const authorizationCode = urlParams.get('code');

    if (authorizationCode) {
      axios.get(`http://localhost:3001/monday-callback?code=${authorizationCode}`)
        .then((response) => {
          const accessToken = response.data.access_token;
          monday.setToken(accessToken);

          if (accessToken) {
            setToken(accessToken);
            setIsAuthorized(true);
          }
        })
        .catch((error) => {
          console.error('Error exchanging code for tokens:', error);
        });
    }
  }, [monday]);

  const fetchBoardId = async (boardName) => {
    try {
      const response = await fetch("https://api.monday.com/v2", {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${token}`,
        },
        body: JSON.stringify({
          query: `
            {
              me {
                boards {
                  id
                  name
                }
              }
            }
          `,
        }),
      });
      const res = await response.json();
      console.log(res);
      const foundBoard = res.data.me.boards.find(board => board.name === boardName);

      if (foundBoard) {
        return foundBoard.id;
      } else {
        console.error(`Board '${boardName}' not found.`);
        return null;
      }
    } catch (error) {
      console.error('Error fetching board ID:', error);
      return null;
    }
  };

  const fetchInvoices = async () => {
    const userInputBoardName = prompt('Please enter the board name for creating columns:');

    if (!userInputBoardName) {
      window.alert('Board name cannot be empty.');
      return;
    }

   const  boardId = await fetchBoardId(userInputBoardName.trim());
    if (boardId) {
      try {
        const response = await fetch(`http://localhost:3001/invoice`);
        const data = await response.json();

        const Invoices = data.invoices;

        Invoices.forEach(async (invoice) => {
          const columnValues = [];
          columnIDs.forEach(({ id, columnName }) => {
            columnValues.push(`\\"${id}\\":\\"${invoice[columnName.toLowerCase().replace(/\s/g, '')]}\\"`);
          });

          const columnValuesString = `{${columnValues.join(',')}}`;

          const query = `mutation {
            create_item(
              board_id: ${boardId},
              item_name: "Invoices",
              column_values: "${columnValuesString}"
            ) {
              id
            }
          }`;

          try {
            const response = await fetch("https://api.monday.com/v2", {
              method: "post",
              headers: {
                "Content-Type": "application/json",
                'Authorization': `${token}`,
              },
              body: JSON.stringify({
                query: query,
              }),
            });
            const res = await response.json();
            console.log(res);
          } catch (error) {
            console.error("Error creating invoices", error);
          }
        });
        window.alert('Invoices imported successfully!');
      } catch (error) {
        console.error("Error fetching invoices:", error);
        window.alert('Failed to import invoices. Please try again.');
      }
    } else {
      window.alert(`Board '${userInputBoardName}' not found. Cannot import invoices.`);
    }
  };

  const createColumns = async () => {

    const userInputBoardName = prompt('Please enter the board name for creating columns:');

  if (!userInputBoardName) {
    window.alert('Board name cannot be empty.');
    return;
  }

 const  boardId = await fetchBoardId(userInputBoardName.trim());
  if (boardId) {

    const columnsToCreate = ['Invoice Number', 'Reference', 'Status', 'Amount Due'];
    let createdColumnsCount = 0;
    let columnsExist = false;

    try {
      const response = await fetch("https://api.monday.com/v2", {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `${token}`,
        },
        body: JSON.stringify({
          query: `
            {
              boards(ids: ${boardId}) {
                columns {
                  id
                  title
                }
              }
            }
          `,
        }),
      });
      const res = await response.json();

      const existingColumns = res.data.boards[0].columns.map(column => column.title);

      for (const columnName of columnsToCreate) {
        if (!existingColumns.includes(columnName)) {
          const query = `
            mutation {
              create_column (
                board_id: ${boardId},
                title: "${columnName}",
                column_type: text
              ) {
                id
              }
            }
          `;

          try {
            const response = await fetch("https://api.monday.com/v2", {
              method: 'post',
              headers: {
                'Content-Type': 'application/json',
                'Authorization': `${token}`,
              },
              body: JSON.stringify({
                'query': query,
              }),
            });
            const res = await response.json();

            const { id } = res.data.create_column;
            console.log(`Created column '${columnName}':`, id);
            columnIDs.push({ columnName, id });

            createdColumnsCount++;

            if (createdColumnsCount === columnsToCreate.length) {
              window.alert('All columns created successfully!');
            }
          } catch (error) {
            console.error(`Error creating column '${columnName}':`, error);
          }
        } else {
          console.log(`Column '${columnName}' already exists.`);
          columnsExist = true;
        }
      }

      if (columnsExist) {
        window.alert('Columns already exist.');
      }
    } catch (error) {
      console.error('Error fetching columns:', error);
      window.alert('Failed to create columns. Please try again.');
    }
  } else {
    window.alert(`Board '${userInputBoardName}' not found. Cannot create columns.`);
  }
  };





  return (
    <div>
      {isxeroauthorized ? (
        <MondayCard/>

      ) : isauthorized ? (
        <>
          <nav class="navbar navbar-expand-md   pt-5 pb-3  mb-5">
            <div class="container-xxl">
              <img
                src="./favicon.ico" 
                alt="Logo"
                className=" me-2" 
              />
              <span class="text-dark fs-4 fw-bold">Xero Integration</span>

              <div
                class="collapse navbar-collapse justify-content-end align-center "
                id="main-nav"
              >

              </div>
            </div>
          </nav>

          <div className="container  d-grid align-items-center" style={{ height: " 60vh" }}>
            <div className="row justify-content-center align-items-center ">
              <div className="col-8 col-md-6 mb-3">
                <div class="card justify-content-center align-items-center d-grid border-0 shadow" style={{ width: '18 rem', height: '300px' }}>
                  <div class="card-body ">
                    <h2 class="fw-bold mb-3 card-title ">Fetch your Invoices</h2>
                    <button onClick={fetchInvoices} className=" btn btn-primary">
                      Import Invoices
                    </button>
                  </div>
                </div>
              </div>
              <div className="col-8 col-md-6 mb-3">
                <div class="card justify-content-center align-items-center d-grid border-0 shadow" style={{ width: '18 rem', height: '300px' }}>
                  <div class="card-body ">
                    <h2 class="fw-bold mb-3 card-title ">Create columns on monday</h2>
                    <button onClick={createColumns} className=" btn btn-primary">
                      Create Columns
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>




        </>
      ) : (

        <LandingPage/>
      )}
    </div>
  );

};
export default App;
