import React, { useEffect, useState } from "react";
import mondaySdk from "monday-sdk-js";
import './App.css';
//https://23332f08554e13c5.cdn2.monday.app
//http://localhost:3000
function App() {
  const monday = mondaySdk();
  const [isauthorized, setIsAuthorized]= useState(false); 
  const [isxeroauthorized, setIsXeroAuthorized]= useState(false);

  const HandleLogin = () => {

    // Redirect users to Monday.com's authorization URL
    window.location.href = `http://localhost:3001/auth`;
  }


    useEffect (()=>{ 
    // After redirect from Monday.com authorization, handle the received code
    const urlParams = new URLSearchParams(window.location.search);
    const authorizationCode = urlParams.get("code");
    const authorized = urlParams.get("authorized");

    if (authorizationCode) {


      // Exchange the authorization code for an access token and refresh token
      fetch(`http://localhost:3001/monday-callback?code=${authorizationCode}`)
        .then((res) => res.json())
        .then((data) => {

          const accessToken = data.access_token;

          monday.setToken(accessToken);


          if(accessToken){
            console.log(accessToken);
          setIsAuthorized(true);

        }

        })
        .catch((error) => {
          console.error("Error exchanging code for tokens:", error);

        });
    }

    if (authorized === "true") {
      setIsXeroAuthorized(true);
    }

  },[]);


  const HandleXeroLogin = () => {

    // Redirect users to Monday.com's authorization URL
    window.location.href = `http://localhost:3001/connect`;
  }

  // useEffect(() => {
  //   const urlParams = new URLSearchParams(window.location.search);
  //   const authorized = urlParams.get("authorized");

  //   if (authorized === "true") {
  //     setIsXeroAuthorized(true);
  //   }

  //   if (isauthorized && isxeroauthorized) {
  //     console.log("Both authorized");
  //   }
  // }, [isauthorized, isxeroauthorized]);



  return (
    <div>
      {!isauthorized && !isXero?  (
        <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 form-container">
            <center>
              <h3>Connect to Monday!</h3>
            </center>
            <button onClick={HandleLogin} className="btn btn-primary">
               Connect to monday
            </button>
          </div>
        </div>
      </div>
      ): !isxeroauthorized ?  (
         <div className="container">
         <div className="row justify-content-center">
           <div className="col-md-6 form-container">
             <center>
               <h3>Connect to xero!</h3>
             </center>
             <button onClick={HandleXeroLogin} className="btn btn-primary">
               Connect to xero
             </button>
           </div>
         </div>
       </div>
    )  : (
      <div><h1>hello bc</h1></div>
   )}
    </div>
  );
};   
export default App;
